    private void defButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_defButtonActionPerformed
        weightField.setText(""+editedEdge.getDefaultWeight());
    }//GEN-LAST:event_defButtonActionPerformed

    private void saveButtonActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_saveButtonActionPerformed
        try{
            Double weight=Double.parseDouble(weightField.getText());
            if(labelField.getText().isEmpty()){
                JOptionPane.showMessageDialog(rootPane, "Nie podano etykiety");
            }else{
                editedEdge.setWeight(weight);
                String label=labelField.getText();
                if(label.length()>8)label=label.substring(0, 8);
                editedEdge.setLabel(label);
                this.dispose();
            }
        }catch(NumberFormatException ex){
            JOptionPane.showMessageDialog(rootPane, "Podano niepoprawną wagę");
        }
    }//GEN-LAST:event_saveButtonActionPerformed

