    public void setVisible(boolean b,Edge e){
        if(e!=null){
            this.editedEdge=e;
            setVisible(b);
        }
    }

    @Override
    public void setVisible(boolean b) {
        if(this.editedEdge!=null){
            this.setTitle("Edycja "+editedEdge.getLabel());
            this.labelField.setText(editedEdge.getLabel());
            this.weightField.setText(""+editedEdge.getWeight());
            super.setVisible(b);
        }
    }

    
    
    
    

